import {useState, useEffect} from "react"
import { useBudget } from "../../Context/BudgetAndExpense/BudgetAndExpense"

// retrieve data from localStorage
export default function useLocalStorage<T>(key: string, defaultValue: T): [T, React.Dispatch<React.SetStateAction<T>>] {
    const {isLoggedIn} = useBudget() || {}
    const [value, setValue] = useState(() => {
        console.log("useLocalStorage")
        const storedValue = localStorage.getItem(key)
        if (storedValue != null) return JSON.parse(storedValue)
        
        if (typeof defaultValue === "function") {
            return defaultValue()
        } else {
            return defaultValue
        }
    })

    useEffect(() => {
        // 如果在log in的情況下是不會去動到localStorage的
        if (!isLoggedIn) {
            localStorage.setItem(key, JSON.stringify(value))
        }
    }, [key, value, isLoggedIn])

    return [value, setValue]
}
