export const currencyFormatter = new Intl.NumberFormat(undefined, {
    style:"currency",
    currency:"TWD",
    minimumFractionDigits:0
})

export default function getToday(): string {
    const now = Date.now()
    let n = new Date(now),
    y = n.getFullYear(),
    m = n.getMonth() + 1,
    d = n.getDate();
    return y + "-" + (m < 10 ? "0" + m : m) + "-" + (d < 10 ? "0" + d : d)
}
