import React, {FormEvent} from 'react'
import { Modal, Button, Stack } from 'react-bootstrap'
import { UNCATEGORIZED_BUDGETID, useBudget } from '../../Context/BudgetAndExpense/BudgetAndExpense'

export default function ViewExpensesModal({budgetID, handleClose}:{budgetID:string, handleClose:() => React.Dispatch<React.SetStateAction<boolean>>}) {
    const budgetContext = useBudget();
    const { budgets, getBudgetExpenses, deleteBudget, deleteExpense } = budgetContext || {};  

    const budget = budgets?.find((budget) => budget.id === budgetID)

    function handleSubmit(e:FormEvent<HTMLFormElement>) {
        e.preventDefault()
        handleClose()
    }

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>
                    <Stack direction="horizontal" gap={2}>
                        <div>Expense - {budget?.name}</div>
                        {
                            budgetID !== UNCATEGORIZED_BUDGETID && (
                                <Button onClick={() => {deleteBudget!(budgetID); handleClose!()}}
                                variant='outline-danger'>Delete</Button>
                            )
                        }
                    </Stack>
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            </Modal.Body>
        </Modal>
    )
}
