import {v4 as uuidV4} from "uuid"

import { useBudget } from "../../Context/BudgetAndExpense/BudgetAndExpense"
import BudgetCard from "../BudgetCard/BudgetCard"


export default function TotalBudgetAndExpense({showViewExpenseModal}:{showViewExpenseModal:React.Dispatch<React.SetStateAction<string>>}) {
    const {budgets, expenses} = useBudget() || {}
    const totalBudget= budgets?.reduce((total, budget) => total + budget.max, 0)
    const totalExpense = expenses?.reduce((total, expense) => total + expense.amount, 0)

    if (totalExpense === 0) return null

    return (
        <BudgetCard id={uuidV4()} name="總計" max={totalBudget!}
        amount={totalExpense!} gray={true} showButton={false} showViewExpenseModal={showViewExpenseModal}/>
    )
}
