import { useState, useEffect } from "react";

import { budgetObject } from "../../Context/BudgetAndExpense/BudgetAndExpense.tsx";

// Retrieving budget data from the DB
export function useBudgetFromDB(alert: React.Dispatch<React.SetStateAction<boolean>>): [budgetObject[] , React.Dispatch<React.SetStateAction<budgetObject[]>>] {
    const [values, setValues] = useState<budgetObject[]>([]);

    useEffect(() => {
        console.log("fetch budgets from DB")
        const getData = async () => {
            try {
                const response = await fetch("http://localhost:5000/getBudgets", { credentials: "include" });
                if (!response.ok) {
                    console.log("Failed to fetch budget data");
                    alert(true)
                }
                const data = await response.json();
                console.log(data)
                setValues(data);
            } catch (error) {
                console.log("Catch error when fetch budgets data", error);
                alert(true)
            }
        };
        getData();
    }, []); // empty dependency array to run effect only once

    return [values, setValues];
}
