import React, { useState } from 'react'
import { Modal, Button, Stack } from 'react-bootstrap'
import { UNCATEGORIZED_BUDGETID, useBudget } from '../../Context/BudgetAndExpense/BudgetAndExpense'
import { currencyFormatter } from '../../utils';


export default function ViewExpensesModal({show, budgetID, handleClose}:{show:boolean, budgetID:string, handleClose:() => React.Dispatch<React.SetStateAction<boolean>>}) {
    const budgetContext = useBudget();
    const { budgets, getBudgetExpenses, deleteBudget, deleteExpense } = budgetContext || {};  

    const budget = budgets?.find((budget) => budget.id === budgetID)

    const expenses = getBudgetExpenses!(budgetID)

    // state of setting the confirmation window for deleting expenses
    const [confirmDeleteExpenseId, setConfirmDeleteExpenseId] = useState<string | null>(null);

    // Function to handle confirm delete
    const handleConfirmDelete = (expenseId: string) => {
        // Perform deletion action here
        deleteExpense!(expenseId);
        // Close the confirmation modal
        setConfirmDeleteExpenseId(null);
    }

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>
                    <Stack direction="horizontal" gap={2}>
                        {(budget !== undefined && <div>Expense - {budget?.name}</div>) || <div>Expense - 總額</div>}
                        {
                            budgetID !== UNCATEGORIZED_BUDGETID && budgetID !== "total" && (
                                <Button onClick={() => {deleteBudget!(budgetID); handleClose!()}}
                                variant='outline-danger'>Delete</Button>
                            )
                        }
                    </Stack>
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Stack direction='vertical' gap={3}>
                        {
                            expenses.map((expense) => 
                                <div key={expense.id}>
                                    <Stack direction='horizontal' gap={2}>
                                        <div className="me-auto fs-5">{expense.description}</div>
                                        <div className='fw-light'>{expense.date}</div>
                                        <div className="fs-5">{currencyFormatter.format(expense.amount)}</div>
                                        <Button onClick={() => setConfirmDeleteExpenseId(expense.id)} size="sm" variant='outline-danger'>&times;</Button>
                                    </Stack>
                                    <hr className='border border-primary mb-auto'/> {/* Horizontal line */}
                                    {/* Confirmation dialog */}
                                    <Modal show={confirmDeleteExpenseId === expense.id} onHide={() => setConfirmDeleteExpenseId(null)}>
                                        <Modal.Header className='.bg-secondary.bg-gradient' closeButton>
                                            <Modal.Title>確認刪除--<span className='fw-bold'>{expense.description.length > 5 ? (expense.description.slice(0, 5) + "...") : expense.description}</span></Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body className='.bg-secondary.bg-gradient'>
                                            確定要刪除該筆花費嗎? 刪除後無法復原喔
                                        </Modal.Body>
                                        <Modal.Footer className='.bg-secondary.bg-gradient'>
                                            <Button variant="secondary" onClick={() => setConfirmDeleteExpenseId(null)}>取消</Button>
                                            <Button variant="danger" onClick={() => handleConfirmDelete(expense.id)}>確定</Button>
                                        </Modal.Footer>
                                    </Modal>
                                </div>
                                
                            )
                        }
                </Stack>
            </Modal.Body>
        </Modal>
    )
}
