import React, {FormEvent, useRef} from 'react'
import { Modal, Form, Button } from 'react-bootstrap'
import { useBudget } from '../../Context/BudgetAndExpense/BudgetAndExpense'

interface expenseModalProps {
    show: boolean,
    handleClose:() => React.Dispatch<React.SetStateAction<boolean>>,
    defaultBudgetID: string
}

export default function AddExpenseModal({show, handleClose, defaultBudgetID}: expenseModalProps) {
    const budgetContext = useBudget();
    const { addBudget } = budgetContext || {};  

    const nameRef = useRef<HTMLInputElement>(null) // 在typescript裡面使用useRef要注意加上annotation
    const maxRef = useRef<HTMLInputElement>(null)

    function handleSubmit(e:FormEvent<HTMLFormElement>) {
        e.preventDefault()
        if (addBudget) {
            const name = nameRef.current!.value;
            const max = maxRef.current!.value;
            addBudget(name, parseFloat(max));
        }
        
        handleClose()
    }

    return (
        <Modal show={show} onHide={handleClose}>
            <Form onSubmit={handleSubmit}>
                <Modal.Header closeButton>
                    <Modal.Title>New Budget</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group className='mb-3' controlId='name'>
                        <Form.Label>Name</Form.Label>
                        <Form.Control ref={nameRef} type='text' required />
                    </Form.Group>
                    <Form.Group className='mb-3' controlId='max'>
                        <Form.Label>Maximum Spending</Form.Label>
                        <Form.Control ref={maxRef} type='number' required min={0} step={1}/>
                    </Form.Group>
                    <div className="d-flex justify-content-end">
                        <Button variant='primary' type='submit'>Add</Button>
                    </div>
                </Modal.Body>
            </Form>
        </Modal>
    )
}
