import { UNCATEGORIZED_BUDGETID, useBudget } from "../../Context/BudgetAndExpense/BudgetAndExpense"
import { BudgetProps } from "../BudgetCard/BudgetCard"
import BudgetCard from "../BudgetCard/BudgetCard"

const {getBudgetExpenses, budgets} = useBudget() || {}

export default function UncategorizedBudgetCard({toggleModal}:{toggleModal:(budgetID: string) => void}) {
    const max = budgets?.find(budget => budget.id === UNCATEGORIZED_BUDGETID)?.max
    const amount = getBudgetExpenses!(UNCATEGORIZED_BUDGETID).reduce((total, expense) => {
        return total + expense.amount
    }, 0)
    if (amount === 0) return null

    return (
        <BudgetCard id={UNCATEGORIZED_BUDGETID} amount={amount} max={max!} name={UNCATEGORIZED_BUDGETID}
           gray={true} toggleModal={toggleModal}  />
    )
}
