export const currencyFormatter = new Intl.NumberFormat(undefined, {
    style:"currency",
    currency:"TWD",
    minimumFractionDigits:0
})

export default function getData(n: number) {
    let now = new Date(n),
      y = now.getFullYear(),
      m = now.getMonth() + 1,
      d = now.getDate();
    return y + "-" + (m < 10 ? "0" + m : m) + "-" + (d < 10 ? "0" + d : d)
}
