import React, {FormEvent} from 'react'
import { Modal, Button, Stack } from 'react-bootstrap'
import { UNCATEGORIZED_BUDGETID, useBudget } from '../../Context/BudgetAndExpense/BudgetAndExpense'
import { currencyFormatter } from '../../utils';

export default function ViewExpensesModal({show, budgetID, handleClose}:{show:boolean, budgetID:string, handleClose:() => React.Dispatch<React.SetStateAction<boolean>>}) {
    const budgetContext = useBudget();
    const { budgets, getBudgetExpenses, deleteBudget, deleteExpense } = budgetContext || {};  

    const budget = budgets?.find((budget) => budget.id === budgetID)

    const expenses = getBudgetExpenses!(budgetID)

    function handleSubmit(e:FormEvent<HTMLFormElement>) {
        e.preventDefault()
        handleClose()
    }

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>
                    <Stack direction="horizontal" gap={2}>
                        {(budget !== undefined && <div>Expense - {budget?.name}</div>) || <div>Expense - 總額</div>}
                        {
                            budgetID !== UNCATEGORIZED_BUDGETID && budgetID !== "total" && (
                                <Button onClick={() => {deleteBudget!(budgetID); handleClose!()}}
                                variant='outline-danger'>Delete</Button>
                            )
                        }
                    </Stack>
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Stack direction='vertical' gap={3}>
                        {
                            expenses.map((expense) => 
                                <div key={expense.id}>
                                    <Stack direction='horizontal' gap={2}>
                                        <div className="me-auto fs-5">{expense.description}</div>
                                        <div className='fw-light'>24/4/26</div>
                                        <div className="me-auto fs-5">{currencyFormatter.format(expense.amount)}</div>
                                        <Button onClick={() => deleteExpense!(expense.id)} size="sm" variant='outline-danger'>&times;</Button>
                                    </Stack>
                                    <hr className='border border-primary mb-auto'/> {/* Horizontal line */}
                                </div>
                                
                            )
                        }
                </Stack>
            </Modal.Body>
        </Modal>
    )
}
