import {useState, useEffect} from "react"
import { useBudget } from "../../Context/BudgetAndExpense/BudgetAndExpense"

// retrieve data from localStorage
export default function useLocalStorage<T>(key: string, defaultValue: T, isLoggedIn:boolean): [T, React.Dispatch<React.SetStateAction<T>>] {
    const [value, setValue] = useState(() => {
        console.log("useLocalStorage")
        const storedValue = localStorage.getItem(key)
        if (storedValue != null) return JSON.parse(storedValue)
        
        if (typeof defaultValue === "function") {
            return defaultValue()
        } else {
            return defaultValue
        }
    })

    useEffect(() => {
        console.log(isLoggedIn)
        if (!isLoggedIn) {
            console.log("useLocalStorage")
            const storedValue = localStorage.getItem(key)
            if (storedValue != null) setValue(JSON.parse(storedValue))
        }
    }, [isLoggedIn])

    useEffect(() => {
        // 如果在log in的情況下是不會去動到localStorage的
        console.log(isLoggedIn)
        if (!isLoggedIn) {
            localStorage.setItem(key, JSON.stringify(value))
        }
    }, [key, value])



    return [value, setValue]
}
