import React, { useState, useEffect } from 'react';
import './index.css'; // Import CSS for styling

type FlashMsgProps = {
    show: boolean;
    type: string;
    message: string;
};

export default function FlashMsg({ show, type, message }: FlashMsgProps) {
    const [visible, setVisible] = useState(show);

    useEffect(() => {
        setVisible(show); // Update visible state when show prop changes

        if (show) {
            const timer = setTimeout(() => {
                setVisible(false);
            }, 5000); // Hide the flash message after 5 seconds

            return () => clearTimeout(timer); // Cleanup function to clear the timer
        }
    }, [show]); // Add show as a dependency

    return (
        <>
            {visible && (
                <div className={`flash-message ${type}`}>
                    <span className="icon">{type === 'success' ? '✅' : '❌'}</span>
                    <span className="text">{message}</span>
                </div>
            )}
        </>
    );
}
