import React, {useRef, useState} from 'react'
import { Modal, Form, Button, InputGroup } from 'react-bootstrap'
import { useBudget } from '../../Context/BudgetAndExpense/BudgetAndExpense'

export default function UpdateBudgetModal({show, prevBudgetName, handleClose}:{show:boolean,  prevBudgetName:string, handleClose:() => React.Dispatch<React.SetStateAction<boolean>>}) {
    const { updateBudget, budgets } = useBudget() || {};
    const [nameError, setNameError] = useState<boolean>(false);
    const [amountError, setAmountError] = useState<boolean>(false);
    const [displayConfirmUpdateBudget, setDisplayConfirmUpdateBudget] = useState<boolean>(false)

    const handleSubmit = (event: { currentTarget: any; preventDefault: () => void; stopPropagation: () => void; }) => {
        event.preventDefault();
        const form = event.currentTarget;
        console.log(form.checkValidity())
        if (form.checkValidity() === false) {
            event.stopPropagation();
            return
        }

        const name = nameRef.current!.value;
        const max = maxRef.current!.value;
        if (name.trim() === "") {
            alert("名稱不得為空")
            return
        }
        if (budgets!.find(budget => budget.name === name)) {
            alert(`重複的預算名稱--${name}`)
            return
        }

        
        setDisplayConfirmUpdateBudget(true)
        
        //handleClose()
    };

    function handleNameLength(name: string): void {
        if (name.length > 10) {
            setNameError(true)
        } else {
            setNameError(false)
        }
    }

    function handleMaxValue(max: number): void {
        if ( max < 0 ) {
            setAmountError(true)
        } else {
            setAmountError(false)
        }
    }

    function handleConfirmUpdate() {

    }

    const nameRef = useRef<HTMLInputElement>(null) // 在typescript裡面使用useRef要注意加上annotation
    const maxRef = useRef<HTMLInputElement>(null)

    return (
        <Modal show={show} onHide={handleClose}>
            <Form onSubmit={handleSubmit}>
                <Modal.Header closeButton>
                    <Modal.Title>更新預算</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group className='mb-3' controlId="name">
                        <Form.Label>名稱</Form.Label>
                        <InputGroup hasValidation>
                            <Form.Control
                                type="text"
                                ref={nameRef}
                                aria-describedby="inputGroupPrepend"
                                required
                                isInvalid={nameError}
                                onChange={() => {handleNameLength(nameRef.current!.value)}}
                            />
                            <Form.Control.Feedback type="invalid">
                                名稱不得超過12個字元
                            </Form.Control.Feedback>
                        </InputGroup>
                    </Form.Group>
                
                    <Form.Group className='mb-3' controlId='max'>
                        <Form.Label>額度</Form.Label>
                        <InputGroup hasValidation>
                            <Form.Control 
                                ref={maxRef} type='number' required min={0} step={1}
                                isInvalid={amountError}
                                onChange={() => {handleMaxValue(parseInt(maxRef.current!.value))}}/>
                            <Form.Control.Feedback type="invalid">
                                金額只限正整數
                            </Form.Control.Feedback>
                        </InputGroup>
                    </Form.Group>
                    <div className="d-flex justify-content-end">
                        <Button variant='primary' type='submit'>更新</Button>
                    </div>
                    {/* confirm window for updating */}
                    <Modal show={displayConfirmUpdateBudget} onHide={() => setDisplayConfirmUpdateBudget(false)}>
                        <Modal.Header className='bg-light' closeButton>
                            <Modal.Title className='d-flex flex-start flex-wrap'>確認更新--<span className='fw-bold'>
                                {"確定從"}<span className='fw-bold'>{`${prevBudgetName}`}</span>{"改成"}<span className='fw-bold'>{`${nameRef.current!.validationMessage}`}</span>
                                </span></Modal.Title>
                        </Modal.Header>
                        <Modal.Body className='bg-light'>
                            確定要刪除該筆花費嗎? 刪除後無法復原喔
                        </Modal.Body>
                        <Modal.Footer className='bg-light'>
                            <Button variant="secondary" onClick={() => setDisplayConfirmUpdateBudget(false)}>取消</Button>
                            <Button variant="danger" onClick={() => handleConfirmUpdate()}>確定</Button>
                        </Modal.Footer>
                    </Modal>
                </Modal.Body>
            </Form>
        </Modal>
    )
}
