import { Card, ProgressBar, Stack, Button } from "react-bootstrap"

import { currencyFormatter } from "../../utils"

export interface BudgetProps {
  id: string,
  name: string,
  amount: number,
  max: number,
  gray: boolean,
  toggleModal:(budgetID: string) => void
}

export default function BudgetCard( {id, name, amount, max, gray, toggleModal}: BudgetProps) {
  // 設定整個Card的背景色
  const ClassNames: string[] = []
  if (amount > max) {
    ClassNames.push("bg-danger", "bg-opacity-10")
  } else if (gray) {
    ClassNames.push("bg-light")
  }

  return (
    <Card className={ClassNames.join(" ")}>
      <Card.Body>
        <Card.Title className="d-flex justify-content-between align-items-baseline fw-normal mb-3">
          <div className="me-2">{name}</div>
          <div className="d-flex align-items-baseline">
            <span style={{color:`var(${getColor(amount, max)})`}}>{currencyFormatter.format(amount)}</span>
            {
              // 如果沒有設定budget，就不會顯示budget，只會顯示amount
              max && (<span className="text-muted fs-6 ms-1">
              / {currencyFormatter.format(max)}
            </span>)
            }
          </div>
        </Card.Title>
        {
          // 如果沒有設定budget，就不會顯示progress bar
          max && (<ProgressBar className="rounded-pill" variant={getVariant(amount, max)}
          min={0} max={max} now={amount} />)
        }
        
        <Stack direction="horizontal" gap={2} className="mt-4">
          <Button variant="outline-primary" className="ms-auto" onClick={() => toggleModal(id)}>Add Expense</Button>
          <Button variant="outline-secondary">View Expense</Button>
        </Stack>
      </Card.Body>
    </Card>
  )
}

// 判斷amount跟max之間的大小決定文字和progressBar的顏色
function getColor(amount: number, max: number): string {
  if (amount < max) return "--bs-emphasis-color"
  return "--bs-danger"
}

function getVariant(amount: number, max: number): string {
  const ratio = amount / max

  if (ratio < 0.5) return "success"
  if (ratio < 0.8) return "warning"
  return "danger"
}
