import { Container, Stack, Button } from "react-bootstrap"

import BudgetCard from "./Components/BudgetCard/BudgetCard"
import AddBudgetModal from "./Components/AddBudgetModal/AddBudgetModal"
import { useBudget  } from './Context/BudgetAndExpense/BudgetAndExpense'
import "./App.css"
import { useState } from "react"

export default function App() {
  const [showAddBudgetModal, setShowAddBudgetModal] = useState<boolean>(false)
  const budgetContext = useBudget();
  const { budgets, getBudgetExpenses } = budgetContext || {};


  return (
    <>
      <Container className="my-4">
        <Stack direction="horizontal" className="mb-4" gap={2}>
          <h1 className="me-auto">Budget</h1>
          <Button variant="primary" onClick={()=>setShowAddBudgetModal(true)}>Add Budget</Button>
          <Button variant="outline-primary">Add Expense</Button>
        </Stack>
        <div className="cardArea">
            {/* 遍歷所有budgets去跑出budgetCard */}
            {
              budgets!.map((budget) => {
                // 算出該預算的所有目前花費，使用reduce遍歷expense array
                const amount = getBudgetExpenses!(budget.id).reduce((total, expense) => {
                  return total + expense.amount
                }, 0)
                return (
                  <BudgetCard 
                    key={budget.id}
                    name={budget.name}
                    amount={amount}
                    max={budget.max}
                    gray={false}
                  />
                )

              })

            }
        </div>
      </Container>
      <AddBudgetModal show={showAddBudgetModal} handleClose={() => setShowAddBudgetModal(false)!}/>
    </>
  )
}
